import React from 'react'

export const Dropdown = () => {


const dropdownData = [
    {value: 1, name: "Mood"}, 
    {value: 2, name: "Generating Playlist..."}, 
    {value: 3, name: "Swinging to this Song"}, 
]


    return (
        <div>
            <select>
            {dropdownData.map((item,index) => <option key={index} value={item.value}>{item.name}</option>)}
             </select>
        </div>
    )
}

export default Dropdown;
