import "./App.css";
import Dropdown from "./components/Dropdown";
import React, {useState, useEffect} from 'react'

function App() {


const [genre, setGenre] = useState();

  const dropdownData = [
    { value: {genre}, name: "Mood" },
    { value: 2, name: "Generating Playlist..." },
    { value: 3, name: "Visual Orgasm" },
  ];


  useEffect(() => {
    const genreUrl= 'https://theaudiodb.com/api/v1/json/523532/track.php?h=32793500';

    const makeApiCall = () => {
      fetch(genreUrl)
        .then((res) => res.json())
        .then((data) => {
          console.log("genre", data.track[0].strGenre);
          setGenre(data.track[0].strGenre);
        });
    };
    makeApiCall();
  }, []);



  return (
    <form onSubmit={() => {}}>
      <div className="container">
        <Dropdown options={dropdownData} />
        <Dropdown options={dropdownData} />
        <button type="submit">Beam me up Scottaye!</button>
      </div>
    </form>
  );
}

export default App;
